SELECT POST.TITLE, POST.BOARD_ID, REPLY.REPLY_ID, REPLY.WRITER_ID, REPLY.CONTENTS, DATE_FORMAT(REPLY.CREATED_DATE, "%Y-%m-%d") as CREATED_DATE FROM USED_GOODS_BOARD as POST, USED_GOODS_REPLY as REPLY WHERE POST.BOARD_ID = REPLY.BOARD_ID AND POST.CREATED_DATE LIKE "2022-10%" ORDER BY REPLY.CREATED_DATE, POST.TITLE;